
function get_payments (Members, id, cb) {
  Members
    .findOne(id)
    .populate('events_booked')
    .populate('membership_type')
    .populate('payments', {
      where: { date: { '>': new Date(Date.now() - 2 * 365 * 24 * 60 * 60 * 1000) }}
    })
    .exec(cb)
}

module.exports = {
  get_payments
}
