// If member has a primary  email address and their ‘Email bounced’ is set to False then they are Email
// If member has no primary email address OR if ‘Email; bounced’ is set to True then they are Letter

const { both, prop, compose, not } = require('ramda')

const can_email = both(prop('primary_email'), compose(not, prop('email_bounced')))
// (member) => member.primary_email && !member.email_bounced


const full_name =
  member =>
    `${member.first_name || member.title || ''} ${member.last_name || member.primary_email}`


module.exports =
  { full_name
  , can_email
  }
