import { add, subtract, divide, multiply, compose, defaultTo, __ } from 'ramda'

const { round } = Math

export const pence = compose(round, multiply(100), Number, defaultTo(0))

export const plus = amount =>
  compose(divide(__, 100), add(pence(amount)), pence)

export const minus = amount =>
  compose(divide(__, 100), subtract(pence(amount)), pence)

export const plus2 = (a, b) => plus(a)(b)

export const minus2 = (a, b) => minus(a)(b)
