const { curry, over, lensProp, flip } = require('ramda')
const currencyFormatter = require('currency-formatter');


export const trace = curry(function(tag, x) {
  console.log(tag, x);
  return x;
});

// TODO CHANGE NAMES

const poundToPence = str => (parseFloat(str) * 100).toString()

export const convertPounds = over(lensProp('amount'), poundToPence)

export const formatPounds = curry(flip(currencyFormatter.format))({ code: 'GBP' })
