'use strict'

var R = require('ramda')

var sorted_keys = R.curry(
  function (sort, object) { return sort(Object.keys(object)) }
)

var sort_by_array = R.curry(
  function (order, sortee) { return R.sortBy(R.flip(R.indexOf)(order), sortee) }
)

var array_order_keys = R.curry(
  function (array, object) { return sorted_keys(sort_by_array(array), object) }
)

var array_only_keys = R.curry(
  function (array, object) {
    return R.filter(R.flip(R.contains)(array))(array_order_keys(array, object))
  }
)

module.exports =
  { sorted_keys: sorted_keys
  , sort_by_array: sort_by_array
  , array_order_keys: array_order_keys
  , array_only_keys: array_only_keys
  }

