'use strict'

module.exports = function (date_string) {
  if (!date_string || date_string.toString() === new Date(date_string).toISOString()) { return date_string }
  var parts = date_string.split(/[\/-]/)
  return new Date(year_pad(parts[2]), parts[1] - 1, parts[0]).toISOString()
}

function year_pad (year_string) {
  var len = year_string && year_string.length
  return len > 3 ? year_string :
      len === 2 ? '20' + year_string :
      len === 1 ? '200' + year_string :
      year_string }