var R = require('ramda')

exports.exists = R.curry(R.compose(R.not, R.prop))
exports.selected = R.curry(R.compose(R.equals('-- select an option --'), R.prop))
exports.check_tests = function (tests, values) {
  return R.reduce(function (errors, field) {
    return tests[field](field)(values)
      ? R.assoc(field, 'Required', errors)
      : errors
    }, {}, R.keys(tests))
  }
